# kibana server properties# https://www.elastic.co/guide/en/kibana/current/kibana-server-properties.htmldefault['kibana']['port'] = 5600# The host to bind the Kibana server to. Default: host: '0.0.0.0'.default['kibana']['host'] =# The name of the index where saved searched, visualizations,# and dashboards will be stored. Default: kibana_index: .kibana.default['kibana']['index'] =# The page that will be displayed when you launch Kibanadefault['kibana']['app_id'] = 'dashboard'# How long to wait for responses from the Kibana backend or Elasticsearch, in milliseconds.# Default: request_timeout: 500000default['kibana']['request_timeout'] =# How long Elasticsearch should wait for responses from shards.# Set to 0 to disable. Default: shard_timeout: 0.default['kibana']['shard_timeout'] = 0# Indicates whether or not to validate the Elasticsearch SSL certificate.# Set to false to disable SSL verification. Default: verify_ssl: truedefault['kibana']['verify_ssl'] =# The path to the CA certificate for your Elasticsearch instance.# Specify if you are using a self-signed certificate so the certificate can be verified.# (Otherwise, you have to disable verify_ssl.) Default: none.default['kibana']['ca'] =# The path to your Kibana server’s key file.# Must be set to encrypt communications between the browser and Kibana.# Default: none.default['kibana']['ssl_key_file'] =# The path to your Kibana server’s certificate file.# Must be set to encrypt communications between the browser and Kibana.# Default: none.default['kibana']['ssl_cert_file'] =# The location where you want to store the Kibana’s log output.# If not specified, log output is written to standard output and not stored.default['kibana']['log_file'] =# The Elasticsearch instance where the indices you want to query reside.# Default:  elasticsearch_url: "http://localhost:9200".default['elasticsearch']['url'] =# default['elasticsearch']['preserve_host'] =